// src/pages/NoteDetails.js

import React, { useEffect, useState } from "react";
import {  useParams } from "react-router-dom";
import axios from "axios";

const NoteDetails = () => {
  const { id } = useParams();
  console.log("Fetched note ID:", id);
  const [note, setNote] = useState(null);
  const [loading, setLoading] = useState(true);
  console.log("ðŸ§  NoteDetails component mounted");

  useEffect(() => {
    const fetchNote = async () => {
      console.log("Fetching note...");
      try {
        const res = await axios.get(`http://localhost:5000/api/notes/${id}`);
        console.log("Fetched note response:", res.data);
  
        if (res.data && res.data.note) {
          setNote(res.data.note);
        } else {
          console.warn("Note not found in response!");
        }
  
      } catch (error) {
        console.error("Error fetching note:", error.response?.data || error.message);
      } finally {
        setLoading(false);
      }
    };
  
    fetchNote();
  }, [id]);

  if (loading) return <div className="text-center mt-10">Loading note...</div>;

  if (!note) return <div className="text-center mt-10 text-red-500">Note not found</div>;

  return (
    <div className="max-w-3xl mx-auto px-4 py-10">
      <h2 className="text-3xl font-bold text-gray-800 mb-2">{note.title}</h2>
      <p className="text-md text-gray-500 mb-6">Subject: {note.subject}</p>
      <div
        className="prose prose-lg"
        dangerouslySetInnerHTML={{ __html: note.content }}
      ></div>
    </div>
  );
};

export default NoteDetails;
