import React, { useState, useEffect } from "react";
import { Link, Outlet, useNavigate } from "react-router-dom";
import { FaUserCircle, FaBars, FaTimes, FaMoon, FaSun } from "react-icons/fa";

const Dashboard = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [darkMode, setDarkMode] = useState(() => localStorage.getItem("darkMode") === "true");
  const [username, setUsername] = useState(localStorage.getItem("username") || "");
  const [role, setRole] = useState("");
  const [profilePic, setProfilePic] = useState(localStorage.getItem("profilePic") || "https://via.placeholder.com/150");
  const [isFAQOpen, setIsFAQOpen] = useState(false);

  const navigate = useNavigate();

  useEffect(() => {
    const storedRole = localStorage.getItem("role")?.trim().toLowerCase();
    setRole(storedRole || "");
  }, []);

  useEffect(() => {
    document.documentElement.classList.toggle("dark", darkMode);
    localStorage.setItem("darkMode", darkMode);
  }, [darkMode]);

  const toggleMenu = () => setMenuOpen(!menuOpen);
  const toggleDarkMode = () => setDarkMode((prev) => !prev);
  const toggleFAQ = () => setIsFAQOpen(!isFAQOpen);

  const handleLogout = () => {
    localStorage.clear();
    setUsername("");
    setRole("");
    setProfilePic("https://via.placeholder.com/150");
    navigate("/login");
    window.location.reload();
  };

  return (
    <div className="min-h-screen bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-white transition-colors">
      {/* Header */}
      <header className="bg-white dark:bg-gray-800 shadow-md fixed w-full z-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 flex items-center justify-between h-16">
          <h1 className="text-xl font-bold text-blue-600 dark:text-blue-400">NoteHive</h1>
          <nav className="hidden md:flex space-x-6 items-center">
            <Link to="/" className="hover:text-blue-600 dark:hover:text-blue-400">Home</Link>
            <Link to="/about" className="hover:text-blue-600 dark:hover:text-blue-400">About</Link>
            <Link to="/notes" className="hover:text-blue-600 dark:hover:text-blue-400">Notes</Link>
            <Link to="/cp-trivia" className="hover:text-blue-600 dark:hover:text-blue-400">Trivia & Quizzes</Link>
            {role === "admin" && (
              <>
                <Link to="/admin" className="hover:text-blue-600 dark:hover:text-blue-400">Admin Panel</Link>
                <Link to="/admin/noteManager" className="hover:text-blue-600 dark:hover:text-blue-400">Note Manager</Link>
              </>
            )}
            {username ? (
              <div className="relative group">
                <div className="flex items-center cursor-pointer space-x-2">
                  <img src={profilePic} alt="Profile" className="w-8 h-8 rounded-full border-2 border-blue-500" />
                  <span>{username}</span>
                </div>
                <ul className="absolute right-0 mt-2 bg-white dark:bg-gray-700 rounded shadow-md w-40 py-2 opacity-0 group-hover:opacity-100 group-hover:translate-y-0 translate-y-2 transform transition duration-200 z-50">
                  <li onClick={() => navigate("/profile")} className="px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-600 cursor-pointer">Profile</li>
                  <li onClick={() => navigate("/settings")} className="px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-600 cursor-pointer">Settings</li>
                  <li onClick={handleLogout} className="px-4 py-2 text-red-600 hover:bg-gray-100 dark:hover:bg-gray-600 cursor-pointer">Logout</li>
                </ul>
              </div>
            ) : (
              <>
                <Link to="/login" className="hover:text-blue-600 dark:hover:text-blue-400">Login</Link>
                <Link to="/register" className="hover:text-blue-600 dark:hover:text-blue-400">Sign Up</Link>
              </>
            )}
            <button onClick={toggleDarkMode} className="text-xl">
              {darkMode ? <FaSun /> : <FaMoon />}
            </button>
          </nav>
          <button className="md:hidden text-xl" onClick={toggleMenu}>
            {menuOpen ? <FaTimes /> : <FaBars />}
          </button>
        </div>

        {/* Mobile Menu */}
        {menuOpen && (
          <div className="md:hidden bg-white dark:bg-gray-800 px-4 pt-4 pb-6 space-y-3">
            <Link to="/" className="block" onClick={toggleMenu}>Home</Link>
            <Link to="/about" className="block" onClick={toggleMenu}>About</Link>
            <Link to="/notes" className="block" onClick={toggleMenu}>Notes</Link>
            <Link to="/cp-trivia" className="block" onClick={toggleMenu}>Trivia & Quizzes</Link>
            {role === "admin" && (
              <>
                <Link to="/admin" className="block" onClick={toggleMenu}>Admin Panel</Link>
                <Link to="/admin/noteManager" className="block" onClick={toggleMenu}>Note Manager</Link>
              </>
            )}
            {username ? (
              <>
                <div onClick={() => { navigate("/profile"); toggleMenu(); }} className="cursor-pointer">Profile</div>
                <div onClick={() => { navigate("/settings"); toggleMenu(); }} className="cursor-pointer">Settings</div>
                <div onClick={() => { handleLogout(); toggleMenu(); }} className="text-red-600 cursor-pointer">Logout</div>
              </>
            ) : (
              <>
                <Link to="/login" className="block" onClick={toggleMenu}>Login</Link>
                <Link to="/register" className="block" onClick={toggleMenu}>Sign Up</Link>
              </>
            )}
            <button onClick={toggleDarkMode} className="text-xl">{darkMode ? <FaSun /> : <FaMoon />}</button>
          </div>
        )}
      </header>

      {/* Main Content */}
