import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "../css/Notes.css";

const Notes = () => {
  const [notes, setNotes] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchNotes = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get("http://localhost:5000/api/notes", {
          headers: {
          Authorization: `Bearer ${token}`,

          },
        });

        console.log("Fetched notes:", response.data); // âœ… Log fetched notes
        setNotes(response.data);
      } catch (error) {
        console.error("Error fetching notes:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchNotes();
  }, []);

  if (loading) {
    return <div className="loading">Loading notes...</div>;
  }

  return (
    <div className="notes-container">
      <div className="notes-header">
        <h2>My Notes</h2>
        <Link to="/create-note" className="create-note-button">
          + Add New Note
        </Link>
      </div>

      <div className="notes-grid">
        {notes && notes.length === 0 ? (
          <p className="no-notes">No notes available. Create your first note!</p>
        ) : (
          notes.map((note) => (
            <div key={note._id} className="note-card">
              <div className="note-inner">
                {/* Front Side */}
                <div className="note-front">
                  <h3 style={{ color: "white" }}>{note.title}</h3>
                </div>

                {/* Back Side */}
                <div className="note-back">
                  <Link to={/notes/`${note._id}`} className="note-link">
                    View Note
                  </Link>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Notes;
