import React, { useState, useEffect } from "react";
import { Link, useNavigate, Outlet, useLocation, NavLink } from "react-router-dom";
import {
  FaBars,
  FaTimes,
  FaMoon,
  FaSun,
  FaStickyNote,
  FaInfoCircle,
  FaPuzzlePiece,
  FaUsersCog,
  FaSignOutAlt,
  FaHome,
} from "react-icons/fa";
import classNames from "classnames";

const Dashboard1 = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [darkMode, setDarkMode] = useState(() => localStorage.getItem("darkMode") === "true");
  const [username, setUsername] = useState(localStorage.getItem("username") || "");
  const [role, setRole] = useState("");
  const [profilePic, setProfilePic] = useState(
    localStorage.getItem("profilePic") || "https://via.placeholder.com/150"
  );
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    const storedRole = localStorage.getItem("role")?.trim().toLowerCase();
    setRole(storedRole || "");
  }, []);

  useEffect(() => {
    document.body.classList.toggle("dark", darkMode);
    localStorage.setItem("darkMode", darkMode);
  }, [darkMode]);

  const toggleMenu = () => setMenuOpen(!menuOpen);
  const toggleDarkMode = () => setDarkMode((prev) => !prev);

  const handleLogout = () => {
    localStorage.clear();
    setUsername("");
    setRole("");
    setProfilePic("https://via.placeholder.com/150");
    navigate("/login");
    window.location.reload();
  };

  return (
    <div className={`flex h-screen ${darkMode ? "dark bg-gray-900 text-white" : "bg-gray-100 text-gray-800"}`}>
      {/* Sidebar */}
      <aside
        className={`w-64 bg-white dark:bg-gray-800 shadow-lg p-4 flex flex-col fixed md:static z-40 h-screen overflow-y-auto transition-transform duration-300 ease-in-out ${
          menuOpen ? "translate-x-0" : "-translate-x-full"
        } md:translate-x-0`}
      >
        <div className="flex items-center justify-between mb-6">
          <h1 className="text-2xl font-bold text-purple-600">NoteHive</h1>
          <button className="md:hidden" onClick={toggleMenu}>
            <FaTimes size={20} />
          </button>
        </div>

        <nav className="flex flex-col space-y-2 text-base">
          <NavLink
            to="/"
            className={({ isActive }) =>
              classNames("flex items-center gap-2 p-2 rounded-lg transition-colors", {
                "text-purple-600 bg-purple-100 dark:bg-gray-700": isActive,
                "hover:text-purple-600": !isActive,
              })
            }
          >
            <FaHome /> Home
          </NavLink>

          <NavLink
            to="about"
            className={({ isActive }) =>
              classNames("flex items-center gap-2 p-2 rounded-lg transition-colors", {
                "text-purple-600 bg-purple-100 dark:bg-gray-700": isActive,
                "hover:text-purple-600": !isActive,
              })
            }
          >
            <FaInfoCircle /> About
          </NavLink>

          <NavLink
            to="notes"
            className={({ isActive }) =>
              classNames("flex items-center gap-2 p-2 rounded-lg transition-colors", {
                "text-purple-600 bg-purple-100 dark:bg-gray-700": isActive,
                "hover:text-purple-600": !isActive,
              })
            }
          >
            <FaStickyNote /> Notes
          </NavLink>

          <NavLink
            to="cp-trivia"
            className={({ isActive }) =>
              classNames("flex items-center gap-2 p-2 rounded-lg transition-colors", {
                "text-purple-600 bg-purple-100 dark:bg-gray-700": isActive,
                "hover:text-purple-600": !isActive,
              })
            }
          >
            <FaPuzzlePiece /> Trivia
          </NavLink>

          {role === "admin" && (
            <>
              <NavLink
                to="/admin"
                className={({ isActive }) =>
                  classNames("flex items-center gap-2 p-2 rounded-lg transition-colors", {
                    "text-purple-600 bg-purple-100 dark:bg-gray-700": isActive,
                    "hover:text-purple-600": !isActive,
                  })
                }
              >
                <FaUsersCog /> Admin Panel
              </NavLink>

              <NavLink
                to="/admin/noteManager"
                className={({ isActive }) =>
                  classNames("flex items-center gap-2 p-2 rounded-lg transition-colors", {
                    "text-purple-600 bg-purple-100 dark:bg-gray-700": isActive,
                    "hover:text-purple-600": !isActive,
                  })
                }
              >
                <FaStickyNote /> Note Manager
              </NavLink>
            </>
          )}
        </nav>

        <div className="mt-auto pt-4 border-t border-gray-300 dark:border-gray-700">
          {username ? (
            <div className="flex items-center gap-3 mt-2">
              <img
                src={profilePic}
                alt="profile"
                className="w-10 h-10 rounded-full border-2 border-purple-500"
              />
              <div>
                <p className="font-semibold">{username}</p>
                <button
                  onClick={handleLogout}
                  className="text-sm text-red-500 hover:underline flex items-center gap-1 mt-1"
                >
                  <FaSignOutAlt /> Logout
                </button>
              </div>
            </div>
          ) : (
            <div className="flex flex-col space-y-2">
              <Link to="login" className="text-purple-600 hover:underline">
                Login
              </Link>
              <Link to="register" className="text-purple-600 hover:underline">
                Sign Up
              </Link>
            </div>
          )}

          <button
            onClick={toggleDarkMode}
            className="mt-4 flex items-center gap-2 text-sm hover:text-purple-600 transition-colors"
          >
            {darkMode ? <FaSun /> : <FaMoon />} {darkMode ? "Light Mode" : "Dark Mode"}
          </button>
        </div>
      </aside>

      {/* Main Content */}
      <div className="flex-1 overflow-y-auto p-6 md:ml-64 transition-all duration-500 animate-fade-in">
        {/* Top Welcome Header */}
        <div className="sticky top-0 z-30 bg-white dark:bg-gray-900 p-4 shadow-md flex justify-between items-center rounded-lg mb-6">
          <h2 className="text-xl font-semibold">ðŸ‘‹ Welcome Back, {username || "Guest"}!</h2>
          <button onClick={toggleMenu} className="md:hidden text-purple-600 text-xl">
            <FaBars />
          </button>
        </div>

        {/* Render either Outlet or Welcome Content */}
        {location.pathname === "/dashboard" ? (
          <div className="flex flex-col items-center justify-center min-h-[calc(100vh-4rem)]">
            <div className="bg-gradient-to-br from-blue-100 via-white to-purple-100 p-1 rounded-3xl shadow-2xl w-full max-w-5xl">
              <div className="bg-white dark:bg-gray-800 dark:text-white rounded-3xl p-10 sm:p-12 text-center animate-fade-in">
                <h2 className="text-4xl sm:text-5xl font-extrabold text-gray-800 dark:text-white mb-6">
                  Welcome to Your Dashboard ðŸš€
                </h2>
                <p className="text-gray-600 dark:text-gray-300 text-lg sm:text-xl mb-10">
                  Your central hub for managing notes and user information.
                </p>
                <img
                  src="https://images.unsplash.com/photo-1593642532842-98d0fd5ebc1a?w=1000&q=80"
                  alt="Dashboard"
                  className="w-full object-cover max-h-[400px] rounded-2xl"
                />
              </div>
            </div>
          </div>
        ) : (
          <Outlet />
        )}
      </div>
    </div>
  );
};

export default Dashboard1;
