import React, { useState, useEffect } from "react";
import { Link, Outlet, useNavigate } from "react-router-dom";
import { FaUserCircle, FaBars, FaTimes, FaMoon, FaSun } from "react-icons/fa";

const Dashboard1 = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [darkMode, setDarkMode] = useState(() => localStorage.getItem("darkMode") === "true");
  const [username, setUsername] = useState(localStorage.getItem("username") || "");
  const [role, setRole] = useState("");
  const [profilePic, setProfilePic] = useState(localStorage.getItem("profilePic") || "https://via.placeholder.com/150");
  const navigate = useNavigate();

  useEffect(() => {
    const storedRole = localStorage.getItem("role")?.trim().toLowerCase();
    setRole(storedRole || "");
  }, []);

  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add("dark");
    } else {
      document.documentElement.classList.remove("dark");
    }
    localStorage.setItem("darkMode", darkMode);
  }, [darkMode]);

  const toggleMenu = () => setMenuOpen(!menuOpen);
  const toggleDarkMode = () => setDarkMode((prev) => !prev);

  const handleLogout = () => {
    localStorage.clear();
    setUsername("");
    setRole("");
    setProfilePic("https://via.placeholder.com/150");
    navigate("/login");
    window.location.reload();
  };

  return (
    <div className="min-h-screen bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-white transition-colors duration-300">
      {/* Header */}
      <header className="bg-white dark:bg-gray-800 shadow-md fixed w-full z-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 flex items-center justify-between h-16">
          <h1 className="text-2xl font-extrabold tracking-wide text-blue-600 dark:text-blue-400">NotesHive</h1>

          {/* Desktop Nav */}
          <nav className="hidden md:flex items-center space-x-6 text-base font-medium">
            <Link to="/" className="hover:text-blue-600 dark:hover:text-blue-400 transition">Home</Link>
            <Link to="/about" className="hover:text-blue-600 dark:hover:text-blue-400 transition">About</Link>
            <Link to="/notes" className="hover:text-blue-600 dark:hover:text-blue-400 transition">Notes</Link>
            <Link to="/cp-trivia" className="hover:text-blue-600 dark:hover:text-blue-400 transition">Trivia & Quizzes</Link>

            {role === "admin" && (
              <>
                <Link to="/admin" className="hover:text-blue-600 dark:hover:text-blue-400 transition">Admin Panel</Link>
                <Link to="/admin/noteManager" className="hover:text-blue-600 dark:hover:text-blue-400 transition">Note Manager</Link>
              </>
            )}

            {/* Profile Dropdown */}
            {username ? (
              <div className="relative group">
                <div className="flex items-center space-x-2 cursor-pointer">
                  <img src={profilePic} alt="Profile" className="w-8 h-8 rounded-full border-2 border-blue-500" />
                  <span>{username}</span>
                </div>
                <ul className="absolute right-0 mt-3 w-44 bg-white dark:bg-gray-700 border dark:border-gray-600 rounded-lg shadow-lg opacity-0 group-hover:opacity-100 group-hover:translate-y-0 translate-y-2 transform transition-all duration-200 ease-in-out z-50">
                  <li onClick={() => navigate("/profile")} className="px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-600 cursor-pointer">Profile</li>
                  <li onClick={() => navigate("/settings")} className="px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-600 cursor-pointer">Settings</li>
                  <li onClick={handleLogout} className="px-4 py-2 text-red-600 hover:bg-gray-100 dark:hover:bg-gray-600 cursor-pointer">Logout</li>
                </ul>
              </div>
            ) : (
              <>
                <Link to="/login" className="hover:text-blue-600 dark:hover:text-blue-400 transition">Login</Link>
                <Link to="/register" className="hover:text-blue-600 dark:hover:text-blue-400 transition">Sign Up</Link>
              </>
            )}

            {/* Dark Mode Toggle */}
            <button onClick={toggleDarkMode} className="text-xl transition hover:text-yellow-500 dark:hover:text-yellow-400">
              {darkMode ? <FaSun /> : <FaMoon />}
            </button>
          </nav>

          {/* Mobile Toggle */}
          <button className="md:hidden text-xl" onClick={toggleMenu}>
            {menuOpen ? <FaTimes /> : <FaBars />}
          </button>
        </div>

        {/* Mobile Nav */}
        {menuOpen && (
          <div className="md:hidden bg-white dark:bg-gray-800 px-6 py-4 space-y-3 font-medium shadow-md">
            <Link to="/" className="block" onClick={toggleMenu}>Home</Link>
            <Link to="/about" className="block" onClick={toggleMenu}>About</Link>
            <Link to="/notes" className="block" onClick={toggleMenu}>Notes</Link>
            <Link to="/cp-trivia" className="block" onClick={toggleMenu}>Trivia & Quizzes</Link>

            {role === "admin" && (
              <>
                <Link to="/admin" className="block" onClick={toggleMenu}>Admin Panel</Link>
                <Link to="/admin/noteManager" className="block" onClick={toggleMenu}>Note Manager</Link>
              </>
            )}

            {username ? (
              <>
                <div onClick={() => { navigate("/profile"); toggleMenu(); }} className="cursor-pointer">Profile</div>
                <div onClick={() => { navigate("/settings"); toggleMenu(); }} className="cursor-pointer">Settings</div>
                <div onClick={() => { handleLogout(); toggleMenu(); }} className="text-red-600 cursor-pointer">Logout</div>
              </>
            ) : (
              <>
                <Link to="/login" className="block" onClick={toggleMenu}>Login</Link>
                <Link to="/register" className="block" onClick={toggleMenu}>Sign Up</Link>
              </>
            )}

            {/* Dark Mode Toggle for Mobile */}
            <button onClick={toggleDarkMode} className="text-xl mt-2">
              {darkMode ? <FaSun /> : <FaMoon />}
            </button>
          </div>
        )}
      </header>

      {/* Main Content */}
      <main className="pt-20 px-4 sm:px-6 lg:px-8">
        <Outlet />
      </main>
    </div>
  );
};

export default Dashboard1;
