import React, { useState, useEffect } from "react";
import { Link, Outlet, useNavigate } from "react-router-dom";
import {
  FaUserCircle,
  FaBars,
  FaTimes,
  FaMoon,
  FaSun,
  FaStickyNote,
  FaInfoCircle,
  FaPuzzlePiece,
  FaUsersCog,
  FaSignOutAlt,
  FaHome,
} from "react-icons/fa";
 // Keep your custom CSS if needed

const Dashboard1 = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [darkMode, setDarkMode] = useState(() => localStorage.getItem("darkMode") === "true");
  const [username, setUsername] = useState(localStorage.getItem("username") || "");
  const [role, setRole] = useState("");
  const [profilePic, setProfilePic] = useState(localStorage.getItem("profilePic") || "https://via.placeholder.com/150");
  const navigate = useNavigate();

  useEffect(() => {
    const storedRole = localStorage.getItem("role")?.trim().toLowerCase();
    setRole(storedRole || "");
  }, []);

  useEffect(() => {
    document.body.classList.toggle("dark", darkMode);
    localStorage.setItem("darkMode", darkMode);
  }, [darkMode]);

  const toggleMenu = () => setMenuOpen(!menuOpen);
  const toggleDarkMode = () => setDarkMode((prev) => !prev);

  const handleLogout = () => {
    localStorage.clear();
    setUsername("");
    setRole("");
    setProfilePic("https://via.placeholder.com/150");
    navigate("/login");
    window.location.reload();
  };

  return (
    <div className={`flex h-screen ${darkMode ? "dark bg-gray-900 text-white" : "bg-gray-100 text-gray-800"}`}>
      {/* Sidebar */}
      <aside className={`w-64 bg-white dark:bg-gray-800 shadow-lg p-4 flex flex-col transition-transform duration-300 ease-in-out ${menuOpen ? "translate-x-0" : "-translate-x-full"} md:translate-x-0 fixed md:static z-40`}>
        <div className="flex items-center justify-between mb-6">
          <h1 className="text-2xl font-bold text-purple-600">NoteHive</h1>
          <button className="md:hidden" onClick={toggleMenu}>
            <FaTimes size={20} />
          </button>
        </div>
        <nav className="flex flex-col space-y-4">
          <Link to="/" className="hover:text-purple-600 flex items-center gap-2"><FaHome /> Home</Link>
          <Link to="about" className="hover:text-purple-600 flex items-center gap-2"><FaInfoCircle /> About</Link>
          <Link to="notes" className="hover:text-purple-600 flex items-center gap-2"><FaStickyNote /> Notes</Link>
          <Link to="cp-trivia" className="hover:text-purple-600 flex items-center gap-2"><FaPuzzlePiece /> Trivia</Link>

          {role === "admin" && (
            <>
              <Link to="/admin" className="hover:text-purple-600 flex items-center gap-2"><FaUsersCog /> Admin Panel</Link>
              <Link to="/admin/noteManager" className="hover:text-purple-600 flex items-center gap-2"><FaStickyNote /> Note Manager</Link>
            </>
          )}
        </nav>

        <div className="mt-auto pt-4 border-t border-gray-300 dark:border-gray-700">
          {username ? (
            <div className="flex items-center gap-3">
              <img src={profilePic} alt="profile" className="w-10 h-10 rounded-full border-2 border-purple-500" />
              <div>
                <p className="font-semibold">{username}</p>
                <button onClick={handleLogout} className="text-sm text-red-500 hover:underline flex items-center gap-1"><FaSignOutAlt /> Logout</button>
              </div>
            </div>
          ) : (
            <div className="flex flex-col space-y-2">
              <Link to="login" className="text-purple-600 hover:underline">Login</Link>
              <Link to="register" className="text-purple-600 hover:underline">Sign Up</Link>
            </div>
          )}

          <button
            onClick={toggleDarkMode}
            className="mt-4 flex items-center gap-2 text-sm hover:text-purple-600 transition-colors"
          >
            {darkMode ? <FaSun /> : <FaMoon />} {darkMode ? "Light Mode" : "Dark Mode"}
          </button>
        </div>
      </aside>

      {/* Page Content */}
      <div className="flex-1 overflow-y-auto p-6 md:ml-64 transition-all">
        <div className="md:hidden mb-4">
          <button onClick={toggleMenu} className="text-xl text-purple-600">
            <FaBars />
          </button>
        </div>
        {/* <Outlet /> */}

      </div>
    </div>
  );
};

export default Dashboard1;
