import React, { useState, useEffect } from "react";
import { Editor } from "@tinymce/tinymce-react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../css/NoteManager.css";

const NoteManager = () => {
  const navigate = useNavigate();
  const [notes, setNotes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [note, setNote] = useState({ title: "", subject: "", content: "" });
  const [editingNoteId, setEditingNoteId] = useState(null);

  const token = localStorage.getItem("token"); // assuming you store your auth token here

  // Fetch all notes on mount
  useEffect(() => {
    const fetchNotes = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/notes", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setNotes(response.data);
      } catch (error) {
        console.error("Error fetching notes:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchNotes();
  }, [token]);

  const saveNote = async () => {
    const cleanedContent = note.content.replace(/^<p>/, "").replace(/<\/p>$/, "");

    try {
      if (editingNoteId) {
        // Update existing note
        const response = await axios.put(
          `http://localhost:5000/api/notes/${editingNoteId}`,
          { ...note, content: cleanedContent },
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        const updated = notes.map((n) => (n._id === editingNoteId ? response.data.note : n));
        setNotes(updated);
      } else {
        // Create new note
        const response = await axios.post(
          "http://localhost:/api/notes",
          { ...note, content: cleanedContent },
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        setNotes([response.data.note, ...notes]);
      }

      setNote({ title: "", subject: "", content: "" });
      setEditingNoteId(null);
    } catch (error) {
      console.error("Error saving note:", error);
    }
  };

  const editNote = (note) => {
    setEditingNoteId(note._id);
    setNote({ title: note.title, subject: note.subject, content: note.content });
  };

  const deleteNote = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/notes/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setNotes(notes.filter((n) => n._id !== id));
    } catch (error) {
      console.error("Error deleting note:", error);
    }
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div className="note-manager-container">
      <h2>Note Manager</h2>

      <div className="note-editor">
        <input
          type="text"
          placeholder="Title"
          value={note.title}
          onChange={(e) => setNote({ ...note, title: e.target.value })}
          className="note-title-input"
        />
        <input
          type="text"
          placeholder="Subject"
          value={note.subject}
          onChange={(e) => setNote({ ...note, subject: e.target.value })}
          className="note-subject-input"
        />
        <Editor
          apiKey="qks55it6scopg5ibx5k982jzxunxu1x79rdlzkmx2yapv5bb"
          value={note.content}
          onEditorChange={(content) => setNote({ ...note, content })}
          init={{
            height: 250,
            menubar: false,
            plugins: "lists link image table code",
            toolbar: "undo redo | bold italic underline | bullist numlist | link | code",
            content_css: "https://cdn.jsdelivr.net/npm/tinymce@5/skins/ui/oxide/content.css",
          }}
        />
        <button onClick={saveNote} className="save-button">
          {editingNoteId ? "Update Note" : "Add Note"}
        </button>
      </div>

      <h3>All Notes</h3>
      <table className="notes-table">
        <thead>
          <tr>
            <th>Title</th>
            <th>Subject</th>
            <th>Content</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {notes.map((note) => (
            <tr key={note._id}>
              <td>{note.title}</td>
              <td>{note.subject}</td>
              <td dangerouslySetInnerHTML={{ __html: note.content }}></td>
              <td>
                <button onClick={() => editNote(note)} className="edit-button">
                  Edit
                </button>
                <button
                  onClick={() => deleteNote(note._id)}
                  className="delete-button"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default NoteManager;
