import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "../css/Notes.css";

const Notes = () => {
  const [notes, setNotes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedNote, setSelectedNote] = useState(null);

  useEffect(() => {
    const fetchNotes = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get("http://localhost:5000/api/notes", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        setNotes(response.data);
      } catch (error) {
        console.error("Error fetching notes:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchNotes();
  }, []);

  const handleViewNote = async (id) => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get(`http://localhost:5000/api/notes/${id}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      setSelectedNote(response.data);
    } catch (error) {
      console.error("Error fetching note details:", error);
    }
  };

  const closeModal = () => {
    setSelectedNote(null);
  };

  if (loading) {
    return <div className="text-center text-lg font-semibold mt-10">Loading notes...</div>;
  }

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <div className="flex items-center justify-between mb-6">
        <h2 className="text-3xl font-bold text-gray-800">ðŸ“š My Notes</h2>
        <Link
          to="/create-note"
          className="bg-blue-600 hover:bg-blue-700 text-white font-medium px-4 py-2 rounded-lg transition duration-200"
        >
          + Add New Note
        </Link>
      </div>

      <div className="grid sm:grid-cols-2 md:grid-cols-3 gap-6">
        {notes.length === 0 ? (
          <p className="col-span-full text-center text-gray-600 text-lg">
            No notes available. Create your first note!
          </p>
        ) : (
          notes.map((note) => (
            <div
              key={note._id}
              className="bg-gradient-to-br from-blue-500 to-purple-600 text-white rounded-xl shadow-lg p-6 relative overflow-hidden group transform hover:scale-105 transition duration-300"
            >
              <h3 className="text-xl font-semibold mb-3">{note.title}</h3>
              <p className="text-sm text-white/90 mb-2">Subject: {note.subject}</p>
              <button
                onClick={() => handleViewNote(note._id)}
                className="mt-4 bg-white text-blue-700 font-semibold px-3 py-1 rounded-md hover:bg-blue-100 transition duration-200"
              >
                View Note
              </button>
            </div>
          ))
        )}
      </div>

      {/* Modal for selected note */}
      {selectedNote && (
        <div className="fixed inset-0 flex items-center justify-center z-50 bg-black/40 backdrop-blur-sm">
          <div className="bg-white rounded-lg shadow-xl p-6 w-[90%] max-w-2xl relative animate-fadeIn">
            <button
              className="absolute top-2 right-3 text-gray-500 hover:text-gray-700 text-xl"
              onClick={closeModal}
            >
              &times;
            </button>
            <h2 className="text-2xl font-bold mb-2">{selectedNote.title}</h2>
            <p className="text-sm text-gray-500 mb-4">Subject: {selectedNote.subject}</p>
            <div className="text-gray-800 whitespace-pre-wrap">
              {selectedNote.content}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Notes;
